import React from 'react'
import { Outlet, useLocation } from 'react-router-dom';
import Header from './Header';
import Footer from '../common/Footer';
import HeaderIn from '../CommonComponent/HeaderIn/Header_In';

const HeaderLayout = () => {
    const location = useLocation();

  // Define which routes need special header class
  const transparentRoutes = ['/', '/allcategories'];
  const isTransparent = transparentRoutes.includes(location.pathname);
    return (
        <>
        <Header className={isTransparent ? 'header mm-slideout ' : <HeaderIn /> }/>
        <main className="flex-grow-1">
            <Outlet /> {/* This renders the matched child route */}
        </main>
        <Footer />
    </>
    );
}

export default HeaderLayout
