import React, { useContext, useEffect } from "react";
import { useLocation, useNavigate, Link } from "react-router-dom";
import { AppRoutes } from "../../utils/constant";
import { isAuthenticated, logout } from "../../utils/helper/helper";
import { UserContext } from "../../utils/UseContext/UserContext";

function Header() {
  const location = useLocation();
  const navigate = useNavigate();
  const { isUserRegistered, setIsUserRegistered } = useContext(UserContext);

  const handleLogout = () => {
    logout(setIsUserRegistered); // Properly logs out
    navigate("/"); // Optional redirect
  };

  const transparentsRoutes = ["/organization", "/dashboard"];
  const isHeadercls = transparentsRoutes.includes(location.pathname);

  useEffect(() => {
    console.log("isUserRegistered (reactive):", isUserRegistered);
  }, [isUserRegistered]);
  return (
    <header className="header mm-slideout">
      <div id="logo">
        <Link to={AppRoutes.Home} title="Reviews Management App">
          <img
            src="/assets/img/logo.svg"
            width="140"
            height="35"
            alt=""
            className="logo_normal"
          />
          <img
            src="/assets/img/logo_sticky.svg"
            width="140"
            height="35"
            alt=""
            className="logo_sticky"
          />
        </Link>
      </div>

      <ul id="top_menu">
        <li>
          {isAuthenticated() ? (
            <>
              <a
                id="sign-in"
                className="login"
                title="Logout"
                onClick={handleLogout}
              >
                Sign In
              </a>
            </>
          ) : (
            <Link
              to={AppRoutes.LoginPage}
              id="sign-in"
              className="login"
              title="Sign In"
            >
              Sign In
            </Link>
          )}
        </li>
      </ul>

      <a href="#menu" className="btn_mobile">
        <div className="hamburger hamburger--spin" id="hamburger">
          <div className="hamburger-box">
            <div className="hamburger-inner"></div>
          </div>
        </div>
      </a>

      <nav id="menu" className="main-menu">
        <ul>
          <li>
            <span>
              <Link to={AppRoutes.Home}>Home</Link>
            </span>
          </li>
          <li>
            <span>
              {isUserRegistered ? (
                ""
              ) : (
                <Link to={AppRoutes.RegisterPage}>
                  Register
                </Link>
              )}
            </span>
          </li>
          <li>
            <span>
              <Link to={AppRoutes.BlogsPage}>Blogs</Link>
            </span>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
