import React, { useEffect, useState } from 'react';
import { avatar_img } from "../../../utils/constant";
import { loginUser, saveAuth } from "../../../utils/helper/helper";
import { getToken, getTokenType } from '../../../utils/helper/helper';
import { jwtDecode } from "jwt-decode";
const fetchUserData = async () => {
  const token = getToken();
  const tokenType = getTokenType();
  const headers = {
    "Content-Type": "application/json",
    Authorization: `${tokenType} ${token}`,
  };

  let userProfile = {
    firstname: "",
    lastname: "",
    country: "",
  };

  try {
    const decoded = jwtDecode(token);
    
    userProfile.firstname = decoded.firstname || decoded.sub || "User";
    userProfile.lastname = decoded.lastname || "";
    userProfile.country = decoded.country || "";
  } catch (err) {
    console.error("Failed to decode token for user profile:", err);
  }

  let reviews = [];
  let stats = { reviews: 0, reads: 0, useful: 0 };
  const userId = localStorage.getItem("userId");
  try {
    const res = await fetch(
      `https://spiroware.com/reviewssystem/api/v1/user/reviews/${userId}`,
      { headers }
    );
    if (res.ok) {
      const data = await res.json();
      reviews = Array.isArray(data.data) ? data.data : [];
      stats.reviews = reviews.length;
      stats.reads = reviews.length;
      stats.useful = reviews.reduce((sum, r) => sum + (r.likes || 0), 0);
    }
  } catch (err) {
    console.error("Failed to fetch reviews:", err);
  }

  return {
    ...userProfile,
    reviews,
    stats,
  };
};

function Dashboard() {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetchUserData().then(setUserData);
  }, []);

  const handleDelete = async (reviewId) => {
    const userId = localStorage.getItem("userId");
    if (!userId || isNaN(Number(userId))) {
      alert("Invalid user ID");
      return;
    }

    const token = getToken();
    const tokenType = getTokenType();
    const headers = {
      "Content-Type": "application/json",
      Authorization: `${tokenType} ${token}`,
    };

    try {
      const res = await fetch(
        `https://spiroware.com/reviewssystem/api/v1/user/review/delete/${userId}/${reviewId}`,
        { method: "DELETE", headers }
      );

      let result;
      try {
        result = await res.json();
      } catch {
        console.error("Server error: Non-JSON response");
        alert("Server error");
        return;
      }

      console.log("DELETE result:", result);

      if (res.ok && result.success) {
        setUserData((prev) => ({
          ...prev,
          reviews: prev.reviews.filter((review) => review.revId !== reviewId),
          stats: {
            ...prev.stats,
            reviews: prev.stats.reviews - 1,
          },
        }));
      } else {
        alert(result.message || "Failed to delete review.");
      }
    } catch (err) {
      console.error("DELETE error:", err);
      alert("Error deleting review");
    }
  };
  const renderStars = (count) => (
    <>
      {[...Array(5)].map((_, i) => (
        <i key={i} className={`icon_star${i < count ? "" : " empty"}`}></i>
      ))}
    </>
  );
  if (!userData) return <div>Loading...</div>;

  return (
    <>
      {/* <Header /> */}
      <main className="margin_main_container">
        <div className="user_summary">
          <div className="wrapper">
            <div className="container">
              <div className="row">
                <div className="col-md-6">
                  <figure>
                    <img src={avatar_img} alt="User" />
                  </figure>
                  <h1>
                    {userData.firstname} {userData.lastname}
                  </h1>
                  <span>{userData.country}</span>
                </div>
                <div className="col-md-6">
                  <ul>
                    <li>
                      <strong>{userData.stats.reviews}</strong>
                      <a
                        href="#0"
                        className="tooltips"
                        data-bs-toggle="tooltip"
                        data-placement="bottom"
                        title="Reviews written by you"
                      >
                        <i className="icon_star"></i> Reviews
                      </a>
                    </li>
                    <li>
                      <strong>{userData.stats.reads}</strong>
                      <a
                        href="#0"
                        className="tooltips"
                        data-bs-toggle="tooltip"
                        data-placement="bottom"
                        title="Number of people who have read your reviews"
                      >
                        <i className="icon-user-1"></i> Reads
                      </a>
                    </li>
                    <li>
                      <strong>{userData.stats.useful}</strong>
                      <a
                        href="#0"
                        className="tooltips"
                        data-bs-toggle="tooltip"
                        data-placement="bottom"
                        title="Number of people who found your review useful"
                      >
                        <i className="icon_like_alt"></i> Useful
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="container margin_60_35">
          <div className="row">
            <div className="col-lg-8">
              {userData.reviews.length === 0 ? (
                <div>No reviews found.</div>
              ) : (
                userData.reviews.map((review) => (
                  <div className="review_card" key={review.revId}>
                    <div className="row">
                      <div className="col-md-2 user_info">
                        <figure>
                          <img src={avatar_img} alt="User" />
                        </figure>
                        <h5>Review: "{review.subject}"</h5>
                      </div>
                      <div className="col-md-10 review_content">
                       <div className="clearfix add_bottom_15">
                         <span className="rating">
                          {renderStars(review.rating)}
                          <em>{review.rating?.toFixed(2) || "0.00"}/5.00</em>
                         </span>
                          <em>
                         Published: {new Date(review.submittedAt).toLocaleDateString()}
                        </em>
                         </div>
                        <h4>{`"${review.subject}"`}</h4>
                        <p>{review.comment}</p>
                        <p>Likes: {review.likes}, Dislikes: {review.dislikes}</p>
                        <div>
                           {review.taglines?.map((tag, idx) => (
                          <span key={idx} className="badge bg-secondary me-1">{tag}</span>
                             ))}
                           </div>
                        <ul>
                          <li>
                            <a
                              href="#0"
                              className="btn_delete"
                              onClick={() => {
                              if (window.confirm("Are you sure you want to delete this review?")) {
                              handleDelete(review.revId)
                              }
                            }}
                            >
                              <i className="icon-trash"></i> Delete
                            </a>
                          </li>
                          <li>
                            <a href="#0">
                              <i className="icon-edit-3"></i> Edit
                            </a>
                          </li>
                        </ul>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>

            <div className="col-lg-4">
              <div className="box_general general_info">
                <h3>
                  Delete a review <i className="pe-7s-help1"></i>
                </h3>
                <p>
                  <strong>Mucius doctus constituto pri at.</strong> Vix id viris
                  consul honestatis...
                  <br />
                  <strong>
                    <a href="faq.html">Read more</a>
                  </strong>
                </p>
                <hr />
                <h3>
                  Post a review <i className="pe-7s-help1"></i>
                </h3>
                <p>
                  Dolor detraxit duo in...
                  <br />
                  <strong>
                    <a href="faq.html">Read more</a>
                  </strong>
                </p>
                <hr />
                <h3>
                  Approve a review <i className="pe-7s-help1"></i>
                </h3>
                <p>
                  Sed ne prompta insolens mediocrem...
                  <br />
                  <strong>
                    <a href="faq.html">Read more</a>
                  </strong>
                </p>
                <hr />
                <div className="text-center">
                  <a href="faq.html" className="btn_1 small">
                    View all FAQs
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>
      
    </>
  );
}

export default Dashboard;
