import React, { useState } from "react";

export default function ReviewForm() {
  const [rating, setRating] = useState(0);
  const [title, setTitle] = useState("");
  const [review, setReview] = useState("");
  const [photo, setPhoto] = useState(null);

  const handleRating = (value) => {
    setRating(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ rating, title, review, photo });
  };

  return (
    <div style={styles.container}>
      <div style={styles.reviewForm}>
        <h1 style={styles.reviewTitle}>Write a review of Good Electronics</h1>

        <form onSubmit={handleSubmit}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Overall rating</label>
            <div style={styles.stars}>
              {[1, 2, 3, 4, 5].map((star) => (
                <button
                  key={star}
                  type="button"
                  onClick={() => handleRating(star)}
                  style={{
                    ...styles.star,
                    color: rating >= star ? "#facc15" : "#ccc"
                  }}
                >
                  ★
                </button>
              ))}
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Title of your review</label>
            <input
              type="text"
              placeholder="If you could say it in one sentence, what would you say?"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Your review</label>
            <textarea
              rows="6"
              placeholder="Write your review to help others learn about this online business"
              value={review}
              onChange={(e) => setReview(e.target.value)}
              style={{ ...styles.input, resize: "vertical" }}
            ></textarea>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Add your photo (optional)</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setPhoto(e.target.files[0])}
              style={styles.input}
            />
          </div>

          <div style={{ ...styles.formGroup, display: "flex", alignItems: "flex-start", gap: "0.5rem" }}>
            <input type="checkbox" />
            <p style={styles.terms}>
              Eos tollit ancillae ea, lorem consulatu qui ne, eu eros eirmod scaevola sea. Et nec tantas
              accusamus salutatus, sit commodo veritus te, erat legere fabulas has ut. Rebum laudem cum ea,
              ius essent fuisset ut. Viderer petentium cu his.
            </p>
          </div>

          <button type="submit" style={styles.submitButton}>
            Submit review
          </button>
        </form>
      </div>


      <div style={styles.sidebar}>
        <h2 style={styles.sidebarTitle}>Recent reviews for Good Electronics</h2>

        {[1, 2].map((index) => (
          <div key={index} style={styles.reviewCard}>
            <div style={styles.cardHeader}>
              <img
                src="https://www.google.com/imgres?q=man%20images&imgurl=https%3A%2F%2Fplus.unsplash.com%2Fpremium_photo-1732117940281-e1598445016c%3Ffm%3Djpg%26q%3D60%26w%3D3000%26ixlib%3Drb-4.1.0%26ixid%3DM3wxMjA3fDB8MHxzZWFyY2h8MXx8YW1lcmljYW4lMjBtYW58ZW58MHx8MHx8fDA%253D&imgrefurl=https%3A%2F%2Funsplash.com%2Fs%2Fphotos%2Famerican-man&docid=Gdpda1kcMKksBM&tbnid=CYl2PF3L34f8-M&vet=12ahUKEwijkfW50rSNAxUkxDgGHdpFIvoQM3oECDcQAA..i&w=3000&h=4500&hcb=2&ved=2ahUKEwijkfW50rSNAxUkxDgGHdpFIvoQM3oECDcQAA"
                alt="User"
                style={styles.avatar}
              />
              <div>
                <div style={styles.starsInline}>
                  {"★★★★☆"} <span style={styles.ratingText}>4.50/5.00</span>
                </div>
                <div style={styles.shopText}>Shops</div>
              </div>
            </div>
            <div style={styles.cardBody}>
              <strong>Jhon Doe</strong>
              <p>"Awesome Experience"</p>
              <p style={styles.snippet}>
                Et nec tantas accusamus salutatus, sit commodo veritus te
              </p>
              <p style={styles.date}>26.08.2018</p>
              <button style={styles.readButton}>Read review</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    gap: "2rem",
    padding: "2rem",
    fontFamily: "Arial, sans-serif"
  },
  reviewForm: {
    flex: 2
  },
  sidebar: {
    flex: 1,
    backgroundColor: "#f8fafc",
    padding: "1rem",
    borderRadius: "8px"
  },
  sidebarTitle: {
    fontSize: "1.25rem",
    fontWeight: "bold",
    marginBottom: "1rem"
  },
  reviewTitle: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "1rem"
  },
  formGroup: {
    marginBottom: "1.25rem"
  },
  label: {
    display: "block",
    fontSize: "1.125rem",
    fontWeight: 600,
    marginBottom: "0.5rem"
  },
  input: {
    width: "100%",
    border: "1px solid #ccc",
    borderRadius: "4px",
    padding: "0.5rem",
    fontSize: "1rem"
  },
  stars: {
    display: "flex",
    gap: "0.5rem"
  },
  star: {
    fontSize: "1.75rem",
    background: "none",
    border: "none",
    cursor: "pointer"
  },
  terms: {
    fontSize: "0.875rem",
    color: "#555"
  },
  submitButton: {
    backgroundColor: "#2563eb",
    color: "white",
    padding: "0.5rem 1rem",
    border: "none",
    borderRadius: "4px",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "background-color 0.3s ease"
  },
  reviewCard: {
    backgroundColor: "white",
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "1rem",
    marginBottom: "1rem"
  },
  cardHeader: {
    display: "flex",
    gap: "0.75rem",
    alignItems: "center"
  },
  avatar: {
    width: "40px",
    height: "40px",
    borderRadius: "50%"
  },
  starsInline: {
    fontSize: "1rem",
    color: "#16a34a"
  },
  ratingText: {
    marginLeft: "0.5rem",
    color: "#555"
  },
  shopText: {
    fontSize: "0.875rem",
    color: "#666"
  },
  cardBody: {
    marginTop: "0.75rem"
  },
  snippet: {
    fontSize: "0.875rem",
    color: "#444"
  },
  date: {
    fontSize: "0.75rem",
    color: "#888"
  },
  readButton: {
    marginTop: "0.5rem",
    backgroundColor: "#3b82f6",
    color: "#fff",
    padding: "0.25rem 0.75rem",
    border: "none",
    borderRadius: "4px",
    fontSize: "0.875rem",
    cursor: "pointer"
  }
};
